import React, { useEffect, useState } from 'react';
import './Navbar.css';
import { NavLink } from 'react-router-dom';

const Navbar = () => {
  const [isMobileNavOpen, setMobileNavOpen] = useState(false);

  const toggleMobileNav = () => {
    setMobileNavOpen(!isMobileNavOpen);
  };

  const closeMobileNav = () => {
    setMobileNavOpen(false);
  };

  const handleDropdown = (e) => {
    const clickedElement = e.target;

    if (clickedElement.matches('.navbar .dropdown > span')) {
      const dropdown = clickedElement.nextElementSibling;
      dropdown.classList.toggle('dropdown-active');
    }
  };

  useEffect(() => {
    const handleClick = (e) => {
      const clickedElement = e.target;
      const navbar = document.getElementById('navbar');

      if (clickedElement.matches('.mobile-nav-toggle')) {
        toggleMobileNav();
      }

      if (clickedElement.matches('.navbar .abc')) {
        closeMobileNav();
      }

      if (clickedElement.matches('.scrollto')) {
        const targetHash = clickedElement.getAttribute('to');

        if (targetHash && document.querySelector(targetHash)) {
          e.preventDefault();
          closeMobileNav();
          scrollto(targetHash);
        }
      }

      handleDropdown(e);
    };

    document.addEventListener('click', handleClick);

    return () => {
      document.removeEventListener('click', handleClick);
    };
  }, []);

  const scrollto = (hash) => {
    let header = document.getElementById('header');
    let offset = header.offsetHeight;

    let cleanedHash = hash.replace('/', '');
    let element = document.querySelector(cleanedHash);

    if (element) {
      let elementPos = element.offsetTop;
      window.scrollTo({
        top: elementPos - offset,
        behavior: 'smooth',
      });
    }
  };

  return (
    <>
      <header id="header" className="fixed-top">
        <div className="container d-flex align-items-center justify-content-between">
          <h1 className="logo">
            <NavLink to="/">Medicare</NavLink>
          </h1>

          <nav id="navbar" className={`navbar ${isMobileNavOpen ? 'navbar-mobile' : ''}`}>
            <ul onClick={handleDropdown}>
              {/* ... your existing code ... */}
              <li><NavLink className="nav-link scrollto abc" to="/Home">Home</NavLink></li>
                             <li><NavLink className="nav-link scrollto abc" to="/About">About</NavLink></li>
                             <li><NavLink className="nav-link scrollto abc" to="/FAQ">FAQ's</NavLink></li>
                            <li><NavLink className="nav-link scrollto abc" to="/Contact">Contact</NavLink></li>
                         
                             <li className="dropdown"><NavLink to="#"><span>Login / Signup</span> <i className="bi bi-chevron-down"></i></NavLink>
                                 <ul>
                                     <li><NavLink to="/Login">Login</NavLink></li>
                                     <li><NavLink to="/Signup">Signup</NavLink></li>
                                   
                                 </ul>
                             </li>
                             <li><NavLink className="nav-link scrollto" to="/Logout">Logout</NavLink></li>
                             <li><NavLink className="getstarted scrollto" to="/Book">Book Appointment</NavLink></li>
            </ul>
            <i className="bi bi-list mobile-nav-toggle" onClick={toggleMobileNav}></i>
          </nav>
        </div>
      </header>
    </>
  );
};

export default Navbar;


