import React from 'react'
import './Home.css'
import { NavLink } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';



const Home = () => {
    const navigate = useNavigate();

    const appointment = async () => {
        

        try {
            const response = await fetch('/add-appointment', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(),
            });

            const res = await response.json();
            console.log(response.status);

            if (response.status === 500 || !res) {
                // toast.error("Internal server error");
                window.alert("Internal server error");
                console.log("Internal server error");
            }
            else if (response.status === 401) {
                // toast.error("Please Login");
                window.alert("Please Login");
                console.log("Please Login");
                navigate("/login");
            }
           
            else if (response.status === 201) {
                // toast.success("Appointment Successfull");
                window.alert("Appointment Successfull");
                console.log("Appointment Successfull");
            }
            else {
                // toast.error("Internal Error");
                window.alert("Internal Error");
                console.log("Internal Error");
            }

        } catch (error) {
            console.error(error);
        }
    };
    return (
        <>
            <section id="hero" className="d-flex align-items-center">

                <div className="container d-flex flex-column align-items-center justify-content-center" data-aos="fade-up">
                    <h1>Empowering Your Health Journey</h1>
                    <h2>Explore seamless healthcare with Medicare </h2>
                    <button  className="btn-get-started scrollto" onClick={appointment}>Book Appointment</button>
                    <img src="assets/img/hero-img.png" className="img-fluid hero-img" alt="" data-aos="zoom-in" data-aos-delay="150" />
                </div>

            </section>

        </>
    )
}

export default Home
