import React, { useState, useEffect } from 'react'
import './Medicine.css'

const Medicine = () => {

  const [medicines, setMedicines] = useState([]);

  const getMedicineData = async () => {
    try {

      const response = await fetch("http://localhost:6500/api/admin/getMedicine", {
        method: "GET",
        // headers: {
        //   Authorization: `Bearer ${token}`,
        // },
      });

      const responseData = await response.json();
      // console.log(`users : ${responseData}`);
      setMedicines(responseData);




    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    getMedicineData();
  }, [])

  return (
    <>
      <section id="medicine">
        <div class="container" data-aos="fade-up">

          <div className="section-title">
            <h2>Medicine</h2>
            {/* <p>Magnam dolores commodi suscipit. Necessitatibus eius consequatur ex aliquid fuga eum quidem. Sit sint consectetur velit. Quisquam quos quisquam cupiditate. Et nemo qui impedit suscipit alias ea. Quia fugiat sit in iste officiis commodi quidem hic quas.</p> */}
          </div>
          <div class="searchBox">

            <input type="text" name="search" id="search" placeholder="Search what you want to learn" autofocus />
            <input type="button" name="submit" value="Search" />
          </div>

          <div class="row">


            {/* <div class="col-lg-6">
              <div class="card card-medicine">
                <img src="assets/img/card.jpg" class="card-img-top" alt="..." />
                <div class="card-body">
                  <h5 class="card-title mx-auto">Aspirin</h5>
                  <h6><span>Doasge : </span>325 mg</h6>
                  <div class="card-side-effect">
                    <h6><span>Side Effects :</span></h6>
                    <ul >
                      <li>Nausea</li>
                      <li>Stomach pain</li>
                    </ul>
                  </div>
                  <div class="card-symptoms">

                    <h6><span>Symptoms</span></h6>
                    <ul>
                      <li>Headache</li>
                      <li>Fever</li>
                      <li>Muscle pain</li>
                    </ul>
                  </div>
                  <h6><span>How to Use :</span></h6>
                  <p class="card-text">Take 1 tablet every 4-6 hours as needed for pain relief.</p>
                </div>
              </div>
            </div> */}

            {/* from databse */}
            {medicines.map(medicine => (
              <div key={medicine._id} className="col-lg-6">
                <div className="card card-medicine">
                  <div className="card-body">
                    <h5 className="card-title mx-auto">{medicine.name}</h5>
                    <h6><span>Dosage: </span>{medicine.dosage}</h6>
                    <div className="card-side-effect">
                      <h6><span>Side Effects:</span></h6>
                      <ul>
                        {medicine.sideEffects.map((effect, index) => (
                          <li key={index}>{effect}</li>
                        ))}
                      </ul>
                    </div>
                    <div className="card-symptoms">
                      <h6><span>Symptoms:</span></h6>
                      <ul>
                        {medicine.symptoms.map((symptom, index) => (
                          <li key={index}>{symptom}</li>
                        ))}
                      </ul>
                    </div>
                    <h6><span>How to Use:</span></h6>
                    <p className="card-text">{medicine.usageInstructions}</p>
                  </div>
                </div>
              </div>
            ))}


          </div>




        </div>
      </section>


    </>
  )
}

export default Medicine
